import React, { useState } from "react";
import Card from "../Ui/Card.js";
import ExpenseFilter from "./ExpenseFilter.js";
import ExpenseItem from "./ExpenseItem.js";
import "./Expenses.css";
const Expenses = (prop) => {
  const [selectedYear, setYear] = useState("2021");
  const filterYear = (selectedyear) => {
    setYear(selectedyear);
  };
  const FilterData = prop.item.filter((mandeep) => {
    return mandeep.date.getFullYear().toString() === selectedYear;
  });
  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter selected={selectedYear} onDropDownHandler={filterYear} />
        {FilterData.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
