import React, { useState } from "react";
import Card from "../Ui/Card.js";
import ExpenseFilter from "./ExpenseFilter.js";
import ExpenseItem from "./ExpenseItem.js";
import "./Expenses.css";
const Expenses = (prop) => {
  const [oldyear, setYear] = useState("2020");

  const filterChnageHandler = (selectedYear) => {
    setYear(selectedYear);
  };
  return (
    <Card className="expenses">
      <ExpenseFilter selected={oldyear} onChangeFilter={filterChnageHandler} />
      <ExpenseItem
        title={prop.item[0].title}
        amount={prop.item[0].amount}
        date={prop.item[0].date}
      />
      <ExpenseItem
        title={prop.item[1].title}
        amount={prop.item[1].amount}
        date={prop.item[1].date}
      />

      <ExpenseItem
        title={prop.item[2].title}
        amount={prop.item[2].amount}
        date={prop.item[2].date}
      />
    </Card>
  );
};

export default Expenses;
